import React, { useState, useEffect } from 'react';
import {
  Navbar,
  NavbarToggler,
  Collapse,
  Nav,
  NavItem,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  NavLink,
  InputGroup,
  InputGroupText,
  Button,
  Modal,
  ModalBody,
  Container,
  Row,
  Col,
  Input,
} from 'reactstrap';
import { FaSearch, FaShoppingCart, FaFacebook, FaInstagram, FaTwitter } from 'react-icons/fa';
import './Pagina1.css';

function Pagina1() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);
  const [sticky, setSticky] = useState(false);
  const [searchModalOpen, setSearchModalOpen] = useState(false);

  // Lista de categorías con imágenes únicas
  const categories = [
    { 
      name: "Frutas y Verduras", 
      path: "/productos/frutas-y-verduras",
      image: "data:image/jpeg;base64,"
    },
    { 
      name: "Lácteos y Huevos", 
      path: "/productos/lacteos-y-huevos",
      image: "data:image/jpeg;base64,"
    },
    { 
      name: "Carnes y Pescados", 
      path: "/productos/carnes-y-pescados",
      image: "data:image/jpeg;base64,"
    },
    { 
      name: "Congelados", 
      path: "/productos/congelados",
      image: "data:image/jpeg;base64,"
    },
    { 
      name: "Snacks y Golosinas", 
      path: "/productos/snacks-golosinas",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQVcOeRW0upbuOerYDjKilDvrp9K9vw2NUXg&s"
    },
    { 
      name: "Bebidas", 
      path: "/productos/bebidas",
      image: "https://gestoriapastor.org/wp-content/uploads/2021/01/the-refrescos.jpg"
    },
    { 
      name: "Cereales y Desayunos", 
      path: "/productos/cereales-desayunos",
      image: "data:image/jpeg;base64,"
    },
    { 
      name: "Hogar y Limpieza", 
      path: "/productos/hogar-limpieza",
      image: "https://www.gob.mx/cms/uploads/article/main_image/106856/Herramientas_de_limpieza_tarjeta_redonda_31912667.jpg"
    },
    { 
      name: "Productos de Belleza y Cuidado Personal", 
      path: "/productos/belleza-cuidado-personal",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSNWlycRLM0O9OYz4DoGWTtFld83Tap_rLyhw&s"
    },
    { 
      name: "Bebés y Niños", 
      path: "/productos/bebes-ninos",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbdFyewXELYXioK9qBDoze3kKa07hVAvL31A&s"
    },
  ];

  const toggleNavbar = () => setIsNavbarOpen(!isNavbarOpen);
  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  const toggleSearchModal = () => setSearchModalOpen(!searchModalOpen);

  const handleScroll = () => {
    setSticky(window.scrollY > 50);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div>
      {/* Navbar */}
      <Navbar fixed="top" expand="lg" className={`navbar-light ${sticky ? 'sticky-navbar' : ''}`}>
        <Container>
          <NavbarToggler onClick={toggleNavbar} />
          <Collapse isOpen={isNavbarOpen} navbar>
            <Nav className="w-100 d-flex justify-content-between align-items-center" navbar>
              {/* Sección izquierda */}
              <div className="d-flex align-items-center">
                <NavItem>
                  <NavLink href="/">Inicio</NavLink>
                </NavItem>
                <Dropdown nav isOpen={dropdownOpen} toggle={toggleDropdown}>
                  <DropdownToggle nav caret>Productos</DropdownToggle>
                  <DropdownMenu>
                    {categories.map((category, index) => (
                      <DropdownItem key={index} href={category.path}>
                        {category.name}
                      </DropdownItem>
                    ))}
                  </DropdownMenu>
                </Dropdown>
                <NavItem>
                  <NavLink href="/nosotros">Nosotros</NavLink>
                </NavItem>
                <NavItem>
                  <NavLink href="/contacto">Contacto</NavLink>
                </NavItem>
              </div>

              {/* Sección derecha */}
              <div className="d-flex align-items-center gap-3">
                <InputGroup onClick={toggleSearchModal} style={{ cursor: 'pointer' }}>
                  <InputGroupText className="search-icon">
                    <FaSearch />
                  </InputGroupText>
                </InputGroup>
                <NavItem>
                  <NavLink href="/carrito" className="cart-link">
                    <FaShoppingCart />
                    <span className="cart-count">2</span>
                  </NavLink>
                </NavItem>
              </div>
            </Nav>
          </Collapse>
        </Container>
      </Navbar>

      {/* Hero Section */}
      <div className="hero-section">
        <Container>
          <h1>Bienvenido al Supermercado</h1>
          <p>Encuentra todo lo que necesitas al mejor precio.</p>
          <Button color="primary">Explorar</Button>
        </Container>
      </div>

      {/* Categorías Destacadas */}
      <Container className="categories-section">
        <h2 className="section-title">Categorías Destacadas</h2>
        <Row>
          {categories.map((category, index) => (
            <Col sm="4" key={index}>
              <div className="category-card">
                <img src={category.image} alt={category.name} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{category.name}</h5>
                  <p className="card-text">Explora lo mejor de {category.name}</p>
                  <Button color="primary" href={category.path}>Ver más</Button>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Container>

      {/* Modal de Búsqueda */}
      <Modal isOpen={searchModalOpen} toggle={toggleSearchModal} centered>
        <ModalBody className="search-modal-body">
          <Input type="text" placeholder="Buscar productos..." className="search-modal-input" />
          <Button color="primary" className="search-modal-button">Buscar</Button>
        </ModalBody>
      </Modal>

      {/* Footer */}
      <footer className="footer">
        <Container>
          <Row className="footer-row">
            <Col md="4">
              <h5>Supermercado</h5>
              <p>Tu tienda de confianza para compras diarias. Encuentra todo lo que necesitas al mejor precio.</p>
            </Col>
            <Col md="4">
              <h5>Enlaces</h5>
              <ul className="footer-links">
                <li><a href="/">Inicio</a></li>
                <li><a href="/productos">Productos</a></li>
                <li><a href="/contacto">Contacto</a></li>
              </ul>
            </Col>
            <Col md="4" className="social-icons">
              <h5>Síguenos</h5>
              <a href="#"><FaFacebook /></a>
              <a href="#"><FaInstagram /></a>
              <a href="#"><FaTwitter /></a>
            </Col>
          </Row>
          <Row className="footer-bottom">
            <Col className="text-center">
              <p>&copy; {new Date().getFullYear()} Supermercado. Todos los derechos reservados.</p>
            </Col>
          </Row>
        </Container>
      </footer>
    </div>
  );
}

export default Pagina1;
